t16 expandedState;	// OBSOLETE	// What is the desired colour intensity?	UInt8 colourIntensity;	// OBSOLETE	// How complex would the user like the image?	UInt8 imageDetail;		// OBSOLETE	// Would the user like JPEG format, or use the normal PICT format?	UInt16 fileType;	// // which monitor to create a pattern for (enum monitorCodes or index of monitor)	UInt16 whichMonitor;	// OBSOLETE	}Preferences;extern Preferences gPrefs;void PrefsReset(void);int LoadPreferences(void);void StorePreferences(void);#endif 	//__Preferences__