              /*marking character (if any) or */                                       /* menu ID of submenu (if any)*/                  plain;               /*style of menu item text*/                                    /*second menu item*/            "-",                       /*item text (divider)*/                  noicon, nokey, nomark, plain  /*item characteristics*/      }};resource 'MENU' (1, preload) {	1,	textMenuProc,	0b11111111111111111111111111111111,	enabled,	"File",	{		"New Desktop", noicon, "N", nomark, plain;		"-", noicon, nokey, nomark, plain;		"Open Status", noicon, "O", nomark, plain;		"Close Window", noicon, "W", nomark, plain;		"Save As...", noicon, "S", nomark, plain;		"-", noicon, nokey, nomark, plain;		"Quit", noicon, "Q", nomark, plain;	};};resource 'MENU' (2, preload) {	2,							/* menu ID */	textMenuProc,				/* standard  menu definition */	0b11111111111111111111111000000000, /* enable only the Preferences item */	enabled,					/* enable the title */	"Edit",	{		"Undo", noicon, "Z", nomark, plain;		"-", noicon, nokey, nomark, plain;		"Cut", noicon, "K", nomark, plain;		"Copy", noicon, "C", nomark, plain;		"Paste", noicon, "V", nomark, plain;		"Clear", noicon, nokey, nomark, plain;		"-", noicon, nokey, nomark, plain;		"Select All", noicon, "A", nomark, plain;		"-", noicon, nokey, nomark, plain;		"Preferences...", noicon, ";", nomark, plain;	}};resource 'BNDL' (128) {	'STFS',	0,	{		'ICN#', { 0, 128 },		'FREF', { 0, 128 },	}};resource 'FREF' (128, purgeable) {	'STFS',	0,	""};type 'STFS' { hex byte; };resource 'STFS' (0, purgeable) {	0x00};resource 'vers' (1, purgeable) {	0x02, 0x00, release, 0x00, verUS,	"2.0",	"2.0"};read 'icns' (-16455) "starfish.icns";read 'PICT' (1044) "logopicture.dat";