nomark, plain;		"large", noicon, nokey, nomark, plain;		"full-screen", noicon, nokey, nomark, plain;		"random", noicon, nokey, nomark, plain;	};};/*Palette popup*/resource 'CNTL' (50, preload) {	{43, 20, 59, 224},	0,	visible,	66,	50,	1008,/*1012,*/	1886155892,	"using the"};resource 'MENU' (50, preload) {	50,	textMenuProc,	0xFFFFFFFB,	enabled,	"palettes",	{		"full spectrum", noicon, nokey, nomark, plain;		"random", noicon, nokey, nomark, plain;		"-", noicon, nokey, nomark, plain;		/* The following items should match the list of		   'pltt' resources in srcpalettes.r. */		"blue", noicon, nokey, nomark, plain;		"grey", noicon, nokey, nomark, plain;		"rainbow", noicon, nokey, nomark, plain;		"red", noicon, nokey, nomark, plain;		"seaside", noicon, nokey, nomark, plain;		"autumn", noicon, nokey, nomark, plain;		"summer", noicon, nokey, nomark, plain;		"spring", noicon, nokey, nomark, plain;	};};